import React, { ChangeEvent, Dispatch, KeyboardEvent, MutableRefObject, SetStateAction, forwardRef, useRef } from 'react';
import './style.css';

  //          interface: SearchInput 상자 컴포넌트 Props          //
  interface Props {
    // label: string;
    type: 'text';
    // error: boolean;
    placeholder: string;
    value: string;
    setValue: Dispatch<SetStateAction<string>>;
    icon?: string; //아이콘은 필수가 아니기 때문에 선택자 '?'로 지정
    // errorMessage?: string; //에러가 안 뜰 때가 있기 때문에 선택자 '?'로 지정
    onKeyDown?: (event: KeyboardEvent<HTMLInputElement>) => void;
    onButtonClick?: () => void; //버튼이 없을 수도 있기 때문에 "
  }

  //          component: SearchInput 상자 컴포넌트          //
  const SearchInputBox = forwardRef<HTMLInputElement, Props>((props: Props, ref) => {
    //          state: Properties         //
    const { type, placeholder, value, icon } = props;
    const { setValue, onKeyDown, onButtonClick } = props;

    //            event handler: SearchInput 값 변경 이벤트 처리         //
    const onInputValueChangeHandler = (event: ChangeEvent<HTMLInputElement>) => {
      const value = event.target.value;
      setValue(value);
    };

    //            event handler: SearchInput 값 변경 이벤트 처리         //
    const onKeyDownHandler = (event: KeyboardEvent<HTMLInputElement>) => {
      if (!onKeyDown) return;
      onKeyDown(event);
    };

    //           event handler: 검색 인풋 Enter key down 이벤트 처리         //
    const onSearchEnterKeyDownHandler = (event: KeyboardEvent<HTMLInputElement>) => {
        if (event.key !== 'Enter') return;
        if (!value) return;
    }

    //           event handler: 검색 버튼 클릭 이벤트 처리         //
    const onSearchButtonClickHandler = () => {
    if (!showInput) {
        setShowInput(true);
        return;
    }
    if (!searchValue) {
        setShowInput(false);
        return;
    }
    }

    //          render: Input 상자 렌더링         //
    return (
      <div className='search-inputbox'>
        <div className='search-inputbox-container'>
          <input ref={ref} className='search-input' type={type} placeholder={placeholder} value={value} onChange={onInputValueChangeHandler} onKeyDown={onKeyDownHandler} />
          {onButtonClick !== undefined && (
            <div className='icon-button' onClick={onButtonClick}>
              {icon !== undefined && <div className={icon}></div>}
            </div>
          )}
        </div>
      </div>
    );
  });

  export default SearchInputBox;
